mapper.update:
update stay
set stayname = #{stayName}
, stayloc = #{stayLoc}
, stayadress = #{stayAdress}
, stayinfo = #{stayInfo}
, stayType = #{stayType}
, badge = #{badge}
, latitude = #{latitude}
, longitude = #{longitude}
, stayview = #{stayView}
where stayno = #{stayNo}

update stay
set mainpic1 = #{mainPic1}
where stayno = #{stayNo}

update room
set roomName = #{roomName}
			, info = #{info}
			, roominfo = #{roomInfo}
			, stdperson = #{stdPerson}
			, overperson = #{overPerson}
			, price = #{price}
where roomno = #{roomNo}

update roomoptionyn
set bathroom = #{bathroom}
			, kitchen = #{kitchen}
			, parking = #{parking}
			, babiqu = #{babiqu}
			, pet = #{pet}
			, terrace = #{terrace}
			, pool = #{pool}
			, bed = #{bed}
			, beamprojector = #{beamProjector}
where roomno = #{roomNo}

mapper.select:
  	select *
	from (
			select rownum rn, t2.*
			from(
		        	select to_number(stayno) no, t.* from stay t order by no desc) t2)
	where rn between 1 and 10
