mapper.select:
		SELECT COUNT(*) 
        FROM Message 
        WHERE memberId = #{memberId} 
        AND roomId = #{roomId} 
        AND type = 'ENTER'

		SELECT COUNT(*) 
        FROM Message 
        WHERE memberId = #{memberId} 
        AND roomId = #{roomId} 
        AND type = 'OUT'

  SELECT memberId
	FROM Message
	WHERE roomId = #{roomId}
	  AND type = 'ENTER'
	  AND regdate = (
	    SELECT MIN(regdate)
	    FROM Message
	    WHERE roomId = #{roomId}
	)
